mapper.select:
SELECT w.team_s, w.name_s, w.bp_cd, w.company_r, w.name_r, w.agr_desc, w.agr_detail, w.agr_file, w.plant_cd, (SELECT COUNT(*) FROM ( SELECT item_no FROM BT_PRICE_CHANGE_DTL WHERE plant_cd = w.plant_cd AND bp_cd = w.bp_cd GROUP BY item_no ) a) AS item_count FROM( SELECT /* AgreementMapper.selectAgreementDataOnly */ sender_team as team_s, sender as name_s, bp_cd, bp_nm as company_r, ceo_nm as name_r, agr_desc, agr_detail, agr_file, plant_cd from HT_AGREEMENT WITH (NOLOCK) where plant_cd = #{plantCd} and announced_date = #{announcedDate} and bp_cd in
#{item}
) w

mapper.update:
update HT_AGREEMENT

    agr_detail = #{agrDetail},
    agr_file = #{agrFile},
    reg_dt = GETDATE()


    and doc_no = #{docNo}
    and src_doc_no = #{srcDocNo}
    and plant_cd = #{plantCd}
    and announced_date = #{announcedDate}
    and bp_cd = #{bpCd}
